-- title:   Circle-circle overlap
-- author:  txgruppi, congusbongus
-- desc:    detect overlaps between two circles (or a point)
-- site:    https://github.com/txgruppi
-- license: MIT License
-- version: 0.2
-- script:  lua
-- tags:    overlap circle point physics

local x1 = 5
local y1 = 10
local r1 = 10
local x2 = 240/2
local y2 = 136/2
local r2 = 50

function clamp(min,curr,max)
 if curr<min then
  return min
 end
 if curr>max then
  return max
 end
 return curr
end

function circ_overlap(x1,y1,r1,x2,y2,r2)
    local dx=x1-x2
    local dy=y1-y2
    local r=r1+r2
    return dx*dx+dy*dy<=r*r
end

function TIC()
 if btn(0) then
  y1=y1-1
 end
 if btn(1) then
  y1=y1+1
 end
 if btn(2) then
  x1=x1-1
 end
 if btn(3) then
  x1=x1+1
 end
 if btn(4) then
  r1=r1+1
 end
 if btn(5) then
  r1=r1-1
 end
 y1=clamp(0,y1,135)
 x1=clamp(0,x1,239)
 r1=clamp(0,r1,50)
 
 local c=14
 local overlap=circ_overlap(x1,y1,r1,x2,y2,r2)
 if overlap then
  c=3
 end

 cls(0)
 circb(x1,y1,r1,13)
 circb(x2,y2,r2,c)
 
 print("x1="..x1.." y1="..y1.." x2="..x2.." y2="..y2.." r1="..r1,10,0,15,true,1,true)
 print("overlap == "..tostring(overlap),0,130,15,true,1,true)
end

